import React, { useState, useEffect } from "react";

const AboutUs = () => {
  const [stats, setStats] = useState({
    customers: 0,
    professionals: 0,
    categories: 0
  });
  const [loading, setLoading] = useState(true);

  // Fetch real-time stats from your API
  useEffect(() => {
    const fetchStats = async () => {
      try {
        // Replace with your actual API endpoint
        const response = await fetch('/api/stats');
        const data = await response.json();
        
        setStats({
          customers: data.totalCustomers || 0,
          professionals: data.totalProfessionals || 0,
          categories: data.totalCategories || 0
        });
        setLoading(false);
      } catch (error) {
        console.error('Error fetching stats:', error);
        // Fallback to default values
        setStats({
          customers: 10000,
          professionals: 500,
          categories: 50
        });
        setLoading(false);
      }
    };

    fetchStats();
    
    // Optional: Refresh stats every 5 minutes
    const interval = setInterval(fetchStats, 5 * 60 * 1000);
    
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-b from-slate-50 to-white">
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-blue-600 to-blue-800 text-white py-20">
        <div className="container mx-auto px-4">
          <div className="max-w-4xl mx-auto text-center">
            <h1 className="text-5xl font-bold mb-6">About Us</h1>
            <p className="text-xl text-blue-100">
              Connecting communities with trusted local service professionals
              since our inception
            </p>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-4 py-16">
        {/* Story Section */}
        <div className="max-w-4xl mx-auto mb-16">
          <h2 className="text-3xl font-bold text-center mb-8 text-gray-800">
            Our Story
          </h2>
          <p className="text-lg text-gray-600 leading-relaxed text-center">
            We started with a simple observation: finding reliable local service
            providers shouldn't be a challenge. Whether you need a plumber at
            midnight or an electrician on the weekend, quality help should be
            just a tap away. Today, we're proud to be the bridge between skilled
            professionals and customers who need them.
          </p>
        </div>

