import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const ThreeDotMenu = () => {
  const [showMenu, setShowMenu] = useState(false);

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  const handleLinkClick = () => {
    setShowMenu(false);
  };

  return (
    <>
      <style>{`
        .three-dot-container {
          position: relative;
          display: inline-block;
        }

        .menu-btn {
          background: transparent;
          border: none;
          cursor: pointer;
          padding: 8px;
          display: flex;
          flex-direction: column;
          gap: 4px;
          transition: all 0.3s ease;
          position: relative;
          z-index: 1002;
        }

        .menu-btn:hover {
          transform: scale(1.1);
        }

        .dot {
          width: 6px;
          height: 6px;
          background: #333;
          border-radius: 50%;
          transition: all 0.3s ease;
        }

        .menu-btn:hover .dot {
          background: #667eea;
        }

        .menu-overlay {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background: rgba(0, 0, 0, 0.5);
          z-index: 9999;
          opacity: 0;
          visibility: hidden;
          transition: all 0.3s ease;
          backdrop-filter: blur(3px);
        }

        .menu-overlay.show {
          opacity: 1;
          visibility: visible;
        }

        .menu-panel {
          position: fixed;
          top: 0;
          right: -400px;
          width: 100%;
          max-width: 400px;
          height: 100vh;
          background: white;
          box-shadow: -4px 0 20px rgba(0, 0, 0, 0.2);
          z-index: 10000;
          transition: right 0.3s ease;
          overflow-y: auto;
        }

        .menu-panel.show {
          right: 0;
        }

        .menu-header {
          padding: 20px;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          color: white;
          display: flex;
          justify-content: space-between;
          align-items: center;
        }

        .menu-header h3 {
          margin: 0;
          font-size: 1.5rem;
        }

        .close-btn {
          background: transparent;
          border: none;
          color: white;
          font-size: 2rem;
          cursor: pointer;
          padding: 0;
          width: 40px;
          height: 40px;
          display: flex;
          align-items: center;
          justify-content: center;
          border-radius: 50%;
          transition: all 0.3s ease;
        }

        .close-btn:hover {
          background: rgba(255, 255, 255, 0.2);
          transform: rotate(90deg);
        }

        .menu-content {
          padding: 20px 0;
        }

        .menu-item {
          display: flex;
          align-items: center;
          padding: 16px 30px;
          color: #333;
          text-decoration: none;
          transition: all 0.3s ease;
          border-left: 4px solid transparent;
          font-size: 1.1rem;
        }

        .menu-item:hover {
          background: linear-gradient(90deg, #f8f9ff 0%, #fff 100%);
          border-left-color: #667eea;
          padding-left: 40px;
          color: #667eea;
        }

        .menu-item-icon {
          margin-right: 15px;
          font-size: 1.3rem;
        }

        @media (max-width: 768px) {
          .menu-panel {
            max-width: 300px;
          }
        }
      `}</style>

      <div className="three-dot-container">
        <button 
          className="menu-btn" 
          onClick={toggleMenu}
          aria-label="Menu"
        >
          <span className="dot"></span>
          <span className="dot"></span>
          <span className="dot"></span>
        </button>

        {/* Overlay Background */}
        <div 
          className={`menu-overlay ${showMenu ? 'show' : ''}`}
          onClick={toggleMenu}
        />

        {/* Side Menu Panel */}
        <div className={`menu-panel ${showMenu ? 'show' : ''}`}>
          <div className="menu-header">
            <h3>Menu</h3>
            <button 
              className="close-btn" 
              onClick={toggleMenu}
              aria-label="Close menu"
            >
              √ó
            </button>
          </div>

          <div className="menu-content">
            <a 
              href="#home" 
              className="menu-item"
              onClick={handleLinkClick}
            >
              <span className="menu-item-icon">üè†</span>
              Home
            </a>
            <a 
              href="#about" 
              className="menu-item"
              onClick={handleLinkClick}
            >
              <span className="menu-item-icon">‚ÑπÔ∏è</span>
              About Us
            </a>
            <a 
              href="#login" 
              className="menu-item"
              onClick={handleLinkClick}
            >
              <span className="menu-item-icon">üîê</span>
              Login
            </a>
          </div>
        </div>
      </div>
    </>
  );
};

export default ThreeDotMenu;
