import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const ThreeDotMenu = () => {
  const [showMenu, setShowMenu] = useState(false);

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  const handleLinkClick = () => {
    setShowMenu(false);
  };

  return (
    <>
      <style>{`
        .three-dot-container {
          position: relative;
          display: inline-block;
        }

        .menu-btn {
          background: transparent;
          border: none;
          cursor: pointer;
          padding: 8px;
          display: flex;
          flex-direction: column;
          gap: 4px;
          transition: all 0.3s ease;
        }

        .menu-btn:hover {
          transform: scale(1.1);
        }

        .dot {
          width: 6px;
          height: 6px;
          background: #333;
          border-radius: 50%;
          transition: all 0.3s ease;
        }

        .menu-btn:hover .dot {
          background: #667eea;
        }

        .dropdown-menu-custom {
          position: absolute;
          top: 100%;
          right: 0;
          margin-top: 10px;
          background: white;
          border-radius: 8px;
          box-shadow: 0 4px 20px rgba(0, 0, 0, 0.15);
          min-width: 180px;
          opacity: 0;
          visibility: hidden;
          transform: translateY(-10px);
          transition: all 0.3s ease;
          z-index: 1000;
          overflow: hidden;
        }

        .dropdown-menu-custom.show {
          opacity: 1;
          visibility: visible;
          transform: translateY(0);
        }

        .dropdown-item-custom {
          display: block;
          padding: 12px 20px;
          color: #333;
          text-decoration: none;
          transition: all 0.3s ease;
          border-left: 3px solid transparent;
          cursor: pointer;
        }

        .dropdown-item-custom:hover {
          background: linear-gradient(90deg, #f8f9ff 0%, #fff 100%);
          border-left-color: #667eea;
          padding-left: 25px;
          color: #667eea;
        }

        .menu-overlay {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          z-index: 999;
          background: transparent;
        }
      `}</style>

      <div className="three-dot-container">
        <button 
          className="menu-btn" 
          onClick={toggleMenu}
          aria-label="Menu"
        >
          <span className="dot"></span>
          <span className="dot"></span>
          <span className="dot"></span>
        </button>
        
        <div className={`dropdown-menu-custom ${showMenu ? 'show' : ''}`}>
          <a 
            href="#home" 
            className="dropdown-item-custom"
            onClick={handleLinkClick}
          >
            Home
          </a>
          <a 
            href="#about" 
            className="dropdown-item-custom"
            onClick={handleLinkClick}
          >
            About Us
          </a>
          <a 
            href="#login" 
            className="dropdown-item-custom"
            onClick={handleLinkClick}
          >
            Login
          </a>
        </div>

        {showMenu && (
          <div 
            className="menu-overlay"
            onClick={() => setShowMenu(false)}
          />
        )}
      </div>
    </>
  );
};

export default ThreeDotMenu;
