// src/components/ThreeDotMenu.jsx
import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import { FaEllipsisV } from "react-icons/fa"; // 3-dot icon

const ThreeDotMenu = () => {
  const [open, setOpen] = useState(false);
  const menuRef = useRef();

  // âœ… Close menu if clicked outside
  useEffect(() => {
    const handler = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setOpen(false);
      }
    };
    document.addEventListener("mousedown", handler);
    return () => {
      document.removeEventListener("mousedown", handler);
    };
  }, []);

  return (
    <div className="three-dot-menu" ref={menuRef}>
      {/* 3-dot button */}
      <button
        onClick={() => setOpen(!open)}
        className="btn btn-light border rounded-circle"
      >
        <FaEllipsisV />
      </button>

     {open && (
  <div
    className="dropdown-menu show p-2 shadow"
    style={{
      right: 0,
      left: "auto",
      position: "absolute",
      zIndex: 1050,   // ðŸ”¥ Makes sure it's above dashboard
    }}
  >
    <Link className="dropdown-item" to="/">Home</Link>
    <Link className="dropdown-item" to="/about">About Us</Link>
    <Link className="dropdown-item" to="/login">Login</Link>
    <Link className="dropdown-item" to="/my-account">My Account</Link>
    <Link className="dropdown-item" to="/contact">Contact Us</Link>
  </div>
)}

    </div>
  );
};

export default ThreeDotMenu;
