import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { isLoggedIn, logout, getUserFromToken } from '../utils/auth';
import Navbar from '../components/Navbar';
import API from '../utils/axios';
import { FaFacebookF, FaInstagram, FaTwitter, FaLinkedinIn, FaYoutube, FaChevronLeft, FaChevronRight } from "react-icons/fa";
import './UserDashboard.css';

// âœ… Professional Image Slider Component with pause on hover
const ImageSlider = () => {
  const images = [
    "/service_images/plumber_slide.jpg",
    "/service_images/electrician_slide.jpg",
    "/service_images/carpenter_slide.jpg",
  ];

  const [current, setCurrent] = useState(0);
  const intervalRef = useRef(null);

  const handlePrev = () => {
    setCurrent(prev => (prev === 0 ? images.length - 1 : prev - 1));
  };

  const handleNext = () => {
    setCurrent(prev => (prev === images.length - 1 ? 0 : prev + 1));
  };

  // Start auto-slide
  const startAutoSlide = () => {
    intervalRef.current = setInterval(handleNext, 3000);
  };

  // Stop auto-slide
  const stopAutoSlide = () => {
    if (intervalRef.current) clearInterval(intervalRef.current);
  };

  useEffect(() => {
    startAutoSlide();
    return () => stopAutoSlide();
  }, []); // Run only once on mount

  return (
    <div
      className="position-relative w-100 mt-5"
      onMouseEnter={stopAutoSlide} // pause on hover
      onMouseLeave={startAutoSlide} // resume on leave
    >
      {/* Slides */}
      <div className="overflow-hidden">
        <div
          className="d-flex"
          style={{
            transform: `translateX(-${current * 100}%)`,
            transition: "transform 0.8s ease-in-out",
            width: `${images.length * 100}%`,
          }}
        >
          {images.map((img, i) => (
            <img
              key={i}
              src={img}
              alt={`slide-${i}`}
              className="w-100"
              style={{ height: "300px", objectFit: "cover" }}
            />
          ))}
        </div>
      </div>

      {/* Arrows */}
      <button
        onClick={handlePrev}
        className="btn btn-light position-absolute top-50 start-0 translate-middle-y"
        style={{ borderRadius: "50%", opacity: 0.7 }}
      >
        <FaChevronLeft />
      </button>
      <button
        onClick={handleNext}
        className="btn btn-light position-absolute top-50 end-0 translate-middle-y"
        style={{ borderRadius: "50%", opacity: 0.7 }}
      >
        <FaChevronRight />
      </button>

      {/* Dots */}
      <div className="position-absolute bottom-0 start-50 translate-middle-x mb-3 d-flex gap-2">
        {images.map((_, i) => (
          <div
            key={i}
            onClick={() => setCurrent(i)}
            style={{
              width: "12px",
              height: "12px",
              borderRadius: "50%",
              cursor: "pointer",
              background: current === i ? "#007bff" : "#ccc",
            }}
          />
        ))}
      </div>
    </div>
  );
};

const UserDashboard = () => {
  const [categories, setCategories] = useState([]);
  const [error, setError] = useState('');
  const user = getUserFromToken();
  const navigate = useNavigate();

  useEffect(() => {
    if (!isLoggedIn()) logout();
  }, []);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const res = await API.get('/categories');
        setCategories(res.data);
      } catch (err) {
        setError('Failed to load categories.');
        console.error(err);
      }
    };
    fetchCategories();
  }, []);

  const handleBookClick = (categoryName) => {
    navigate(`/providers/${categoryName.toLowerCase()}`);
  };

  return (
    <>
      <Navbar role="user" />
      <div className="dashboard-container py-5">
        <div className="welcome-box">
          <h2>Welcome</h2>
          <h2>Find and book trusted home services instantly.</h2>
        </div>

        {error && <div className="alert alert-danger mt-3">{error}</div>}

        <h4 className="mt-5 mb-4">Explore Service Categories</h4>
        <div className="row">
          {categories.length > 0 ? (
            categories.map((cat) => (
              <div className="col-md-4 mb-4" key={cat._id}>
                <div className="service-card card h-100 shadow-sm text-center">
                  <div className="card-body d-flex flex-column justify-content-between">
                    <div>
                      <img
                        src={`/service_images/${cat.name.toLowerCase()}_image.jpg`}
                        alt={cat.name}
                        className="img-fluid rounded"
                        style={{ height: '200px', objectFit: 'cover', width: '100%' }}
                      />
                    </div>
                    <button
                      className="btn btn-primary mt-4 w-100"
                      onClick={() => handleBookClick(cat.name)}
                    >
                      View {cat.name} Workers
                    </button>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p className="text-muted">No categories available.</p>
          )}
        </div>
      </div>

      {/* âœ… Slider added here just above footer */}
      <ImageSlider />

      {/* Footer Section */}
      <footer className="custom-footer">
        <div className="container">
          <div className="row">
            <div className="col-md-3">
              <h5>NearbyHelper Company</h5>
              <ul className="list-unstyled">
                <li>About us</li>
                <li>Investor Relations</li>
                <li>Terms & Conditions</li>
                <li>Privacy policy</li>
                <li>Anti-discrimination policy</li>
                <li>ESG Impact</li>
                <li>Careers</li>
              </ul>
            </div>
            <div className="col-md-3">
              <h5>For customers</h5>
              <ul className="list-unstyled">
                <li>NBH reviews</li>
                <li>Categories near you</li>
                <li>Contact us</li>
              </ul>
            </div>
            <div className="col-md-3">
              <h5>For professionals</h5>
              <ul className="list-unstyled">
                <li>Register as a professional</li>
              </ul>
            </div>
            <div className="col-md-3">
              <h5>Follow us</h5>
              <div className="d-flex gap-3">
                <a href="#" className="text-light"><FaFacebookF /></a>
                <a href="#" className="text-light"><FaInstagram /></a>
                <a href="#" className="text-light"><FaTwitter /></a>
                <a href="#" className="text-light"><FaLinkedinIn /></a>
                <a href="#" className="text-light"><FaYoutube /></a>
              </div>
            </div>
          </div>
          <hr className="my-4" />
          <p className="text-center mb-0">Â© {new Date().getFullYear()} Your Company Name. All Rights Reserved.</p>
        </div>
      </footer>
    </>
  );
};

export default UserDashboard;
