// src/components/ThreeDotMenu.jsx
import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import { FiMoreVertical } from "react-icons/fi"; // 3-dot vertical icon
import "./ThreeDotMenu.css";

const ThreeDotMenu = () => {
  const [open, setOpen] = useState(false);
  const menuRef = useRef(null);

  // Close menu if clicked outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div className="three-dot-menu" ref={menuRef}>
      <button className="three-dot-btn" onClick={() => setOpen(!open)}>
        <FiMoreVertical size={22} />
      </button>

      {open && (
        <div className="dropdown-menu">
          <Link to="/" className="dropdown-item">Home</Link>
          <Link to="/about" className="dropdown-item">About Us</Link>
          <Link to="/login" className="dropdown-item">Login</Link>
          <Link to="/account" className="dropdown-item">My Account</Link>
          <Link to="/contact" className="dropdown-item">Contact Us</Link>
        </div>
      )}
    </div>
  );
};

export default ThreeDotMenu;
