// src/components/ThreeDotMenu.jsx
import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const ThreeDotMenu = () => {
  const [open, setOpen] = useState(false);
  const menuRef = useRef(null);
  const navigate = useNavigate();

  const menuItems = [
    { label: "Home", path: "/" },
    { label: "About Us", path: "/about" },
    { label: "Login", path: "/login" },
    { label: "My Account", path: "/account" },
    { label: "My Orders", path: "/orders" },
    { label: "Refer a Worker", path: "/refer-worker" },
    { label: "Verify your Worker, Legally", path: "/verify-worker" },
    { label: "Our Policies +", path: "/policies" },
    { label: "Advertise with us", path: "/advertise" },
    { label: "Affiliations", path: "/affiliations" },
    { label: "Customer Reviews", path: "/reviews" },
    { label: "SalaryTracker", path: "/salary-tracker" },
    { label: "FAQs", path: "/faqs" },
    { label: "Contact Us", path: "/contact" },
  ];

  // Close when clicking outside
  useEffect(() => {
    const handler = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setOpen(false);
      }
    };
    document.addEventListener("click", handler);
    return () => document.removeEventListener("click", handler);
  }, []);

  return (
    <div ref={menuRef} style={{ position: "relative", display: "inline-block" }}>
      {/* 3 Dots */}
      <button
        onClick={() => setOpen(!open)}
        style={{
          background: "transparent",
          border: "none",
          fontSize: "22px",
          cursor: "pointer",
        }}
      >
        â‹®
      </button>

      {/* Dropdown */}
      {open && (
        <div
          style={{
            position: "absolute",
            right: 0,
            top: "30px",
            background: "white",
            border: "1px solid #ddd",
            borderRadius: "8px",
            boxShadow: "0 4px 8px rgba(0,0,0,0.15)",
            zIndex: 100,
            width: "220px",
          }}
        >
          <ul style={{ listStyle: "none", margin: 0, padding: "8px 0" }}>
            {menuItems.map((item, idx) => (
              <li
                key={idx}
                style={{
                  padding: "10px 16px",
                  cursor: "pointer",
                  borderBottom: "1px solid #f1f1f1",
                }}
                onClick={() => {
                  navigate(item.path);
                  setOpen(false);
                }}
              >
                {item.label}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ThreeDotMenu;
